Default AD Constructor: Example and Test




# include <CppAD/CppAD.h>



bool Default(void)

{    bool ok = true;



     using namespace CppAD;



     // default constructor

     AD<double> x, y;



     // check that they are parameters

     ok &= Parameter(x);

     ok &= Parameter(y);



     // assign them values

     x = 3.; 

     y = 4.;



     // just check a simple operation

     ok &= (x + y == 7.);



     return ok;

}




